import { useState, useEffect } from 'react';
import { Zap, TrendingUp } from 'lucide-react';
import { Card, CardContent } from '@/components/ui/card';

interface TokenEarningRateProps {
  dailyPool: number;
  isHolding: boolean;
  className?: string;
}

export default function TokenEarningRate({ dailyPool, isHolding, className = '' }: TokenEarningRateProps) {
  // Calculate tokens per second from daily pool (spread over 24 hours)
  const tokensPerSecond = dailyPool / 86400; // 86400 seconds in a day
  const tokensPerMinute = tokensPerSecond * 60;
  const tokensPerHour = tokensPerMinute * 60;

  const [displayRate, setDisplayRate] = useState(tokensPerSecond);

  useEffect(() => {
    if (!isHolding) return;

    const interval = setInterval(() => {
      // Simulate real-time earning display
      setDisplayRate(prev => prev + 0.01); // Small fluctuation for dynamic feel
    }, 1000);

    return () => clearInterval(interval);
  }, [isHolding]);

  return (
    <Card className={`border-crown-gold/30 ${className}`}>
      <CardContent className="p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Zap className="w-5 h-5 text-crown-gold" />
            <span className="font-medium">Earning Rate</span>
          </div>
          {isHolding && (
            <div className="flex items-center gap-1 text-xs text-gaming-success">
              <TrendingUp className="w-3 h-3" />
              <span>Active</span>
            </div>
          )}
        </div>

        <div className="space-y-2 mt-3">
          <div className="flex justify-between items-center">
            <span className="text-sm text-muted-foreground">Per second:</span>
            <span className="font-gaming font-bold text-crown-gold" data-testid="rate-per-second">
              {tokensPerSecond.toFixed(3)} SAM
            </span>
          </div>
          <div className="flex justify-between items-center">
            <span className="text-sm text-muted-foreground">Per hour:</span>
            <span className="font-gaming text-crown-gold">
              {tokensPerHour.toFixed(0)} SAM
            </span>
          </div>
          <div className="flex justify-between items-center">
            <span className="text-sm text-muted-foreground">Daily pool:</span>
            <span className="font-gaming text-sm">
              {dailyPool.toLocaleString()} SAM
            </span>
          </div>
        </div>

        {!isHolding && (
          <div className="text-xs text-muted-foreground mt-2 text-center">
            Steal the crown to start earning!
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/WorldIDButton.tsx.content:
import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Shield, Check } from 'lucide-react';
import { MiniKit, VerifyCommandInput, VerificationLevel, ISuccessResult } from '@worldcoin/minikit-js';

interface WorldIDButtonProps {
  onVerify?: (verified: boolean) => void;
  isVerified?: boolean;
  className?: string;
}

export default function WorldIDButton({ onVerify, isVerified = false, className = '' }: WorldIDButtonProps) {
  const [isVerifying, setIsVerifying] = useState(false);
  const [isMiniKitInstalled, setIsMiniKitInstalled] = useState(false);

  useEffect(() => {
    setIsMiniKitInstalled(MiniKit.isInstalled());
  }, []);

  const handleVerify = async () => {
    console.log('World ID verification triggered');
    setIsVerifying(true);

    if (!isMiniKitInstalled) {
      console.log('MiniKit not installed, using fallback verification');
      // Fallback for development/testing
      setTimeout(() => {
        setIsVerifying(false);
        onVerify?.(true);
      }, 2000);
      return;
    }

    try {
      const verifyPayload: VerifyCommandInput = {
        action: 'play-and-earn', // Your action ID from Developer Portal
        signal: undefined, // Optional additional data
        verification_level: VerificationLevel.Orb, // Orb verification for humans
      };

      // World App will open verification drawer
      const { finalPayload } = await MiniKit.commandsAsync.verify(verifyPayload);

      if (finalPayload.status === 'error') {
        console.log('Verification error:', finalPayload);
        setIsVerifying(false);
        return;
      }

      // Send proof to backend for verification
      const verifyResponse = await fetch('/api/verify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          payload: finalPayload as ISuccessResult,
          action: 'play-and-earn',
          signal: undefined,
        }),
      });

      const verifyResponseJson = await verifyResponse.json();

      if (verifyResponseJson.status === 200) {
        console.log('Verification success!');
        onVerify?.(true);
      } else {
        console.log('Backend verification failed');
      }
    } catch (error) {
      console.error('World ID verification error:', error);
    } finally {
      setIsVerifying(false);
    }
  };

  if (isVerified) {
    return (
      <Badge variant="secondary" className={`bg-gaming-success text-gaming-success-foreground ${className}`} data-testid="badge-verified">
        <Check className="w-4 h-4 mr-1" />
        Verified Human
      </Badge>
    );
  }

  return (
    <Button
      onClick={handleVerify}
      disabled={isVerifying}
      className={className}
      data-testid="button-world-id-verify"
    >
      <Shield className="w-4 h-4 mr-2" />
      {isVerifying ? 'Verifying...' : 'Verify with World ID'}
    </Button>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

